import React from 'react';
import {  Button, Container, Row, Col, Card } from 'react-bootstrap'
import AppContext from '../AppContext';
import '../App.css'
import img from '../noImage.png'
import Quagga from '@ericblade/quagga2';
class FileScan extends React.Component {
    constructor(props){
        super(props)
        this.state={
        }
        this.triggerInputFile = this.triggerInputFile.bind(this)
    }
    async handleChange(event){
        const files = event.target.files;
        const file = files[0];
        let fileData = await this.toBase64(file);
        this.setState({
            uploadedImage:fileData
        })

    }
    triggerInputFile() { 
        this.fileInput.click() 
    }
    toBase64(file) {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => resolve(reader.result);
            reader.onerror = error => reject(error);
        })
    }
    render(){
        const { barcodeDetected = 'No Barcode Detected!!', uploadedImage } = this.state;
        return (
            <div>
                <Container style={{marginTop:"50px"}}>
                    <Row lg={6}>
                        <Col style={{
                            border: '2px solid #000000'
                        }} lg={6}>
                            {uploadedImage?<img 
                            src={uploadedImage}
                            height="400"
                            width="500"
                            >
                            </img>:
                            <div>
                            <div>
                            <img 
                            src={img}
                            height="400"
                            >
                            </img>
                            </div>
                            Upload a image to scan barcode
                            </div>
                            }

                        </Col>
                        <Col lg={"6"}>
                            <Row style={{ marginTop: "15px", marginLeft: "0", marginRight: "auto" }}>
                                <Card className="text-center" border="secondary" style={{ width: '100%', }}>
                                    <input
                                        ref={fileInput => this.fileInput = fileInput}
                                        type="file"
                                        onChange={this.handleChange.bind(this)}
                                        style={{ display: "none" }}
                                        accept="image/jpeg"
                                    />
                                    <Card.Body style={{ width: '100%', height: "130px" }}>
                                        <Button onClick={this.triggerInputFile}> Upload File </Button>
                                        <p>Upload any JPEG image with barcode</p>
                                    </Card.Body>


                                </Card>

                            </Row>
                            <Row style={{ marginTop: "15px", marginLeft: "0", marginRight: "auto" }}>
                                <Card className="text-center" border="secondary" style={{ width: '100%', }}>
                                    <Card.Header >

                                        Barcode Detected:
                                    </Card.Header>
                                    <Card.Body style={{ width: '100%', height: "130px" }}>

                                        <p>{barcodeDetected}</p>
                                    </Card.Body>
                                </Card>

                            </Row>

                        </Col>
                    </Row>
                </Container>

            </div>

        )
    }



}
FileScan.contextType = AppContext;
export default FileScan;
