import React from 'react';
import Scanner from './Scanner';
import Result from './Result';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            scanning: false,
            results: []
        }

    };


    render() {
        const { results = [], scanning } = this.state;
        return (
            <div>
                <button onClick={this._scan}>{scanning ? 'Stop' : 'Start'}</button>
                <ul className="results">
                    {results.map((result) => (<Result key={result.codeResult.code} result={result} />))}
                </ul>
                {scanning ? <Scanner onDetected={this._onDetected} /> : null}
            </div>
        );
    }

    _scan() {
        this.setState({ scanning: !scanning });
    }

    _onDetected(result) {
        this.setState({ results: this.state.results.concat([result]) });
    }
}
export default App;
