import React from 'react';
import Scanner from './Scanner';
import Result from './Result';

class App extends React.Component {
    constructor(props) {
        super(props);
        this._taxSettings = null;
        this._customerSettings = null;
        this.state = {
            emails: [],
            phoneNumbers: [],
            landLineNumbers: [],
            visitTiers: [],
            tempBillingFiles: [],
            activeTab: 'address',
            addressSearchString: '',
            editAddress: this.props.address,
            isServiceAddress: this.props.address.addressTypeId == 2,
            addressObjectToBeChanged: this.props.address,
            showCompanyName: false,
            syncQuickBooks: this.props.model.flowInvoiceInParent,
            QBsetting: getCacheDataFromLocalStorage('quickBooksSettings'),
            addressDetails: {
                name: '',
                firstname: '',
                lastname: '',
                additionalName: '',
                customerIdentifier: '',
                notes: '',
                email: '',
                landline: '',
                cellphone: '',
                addressLine1: '',
                addressLine2: '',
                city: '',
                state: '',
                country: '',
                zipCode: '',
                ext: '',
                billBlockCustomer: false,
                propertyType: '',
                workSiteType: '',
                workSiteId: '',
                locationCode: '',
                visitChargeTier: null,
                campaign: '',
                enableSAExtraColumns: false,
                companySettings: null,
                isTaxFromWAGov: false,
                isSaleTaxValid: false,
                taxZoneUpdatedBy: '',
                taxZoneUpdatedAt: '',
                paymentTermId: '',
                paymentTermName: '',
                disableLocationNameToogle: false

            },
            hideModalRender: this.props.hideModalRender === true
        };
        this.emailChangeHandler = this.emailChangeHandler.bind(this);
        this.landlineChangeHandler = this.landlineChangeHandler.bind(this);
        this.phoneChangeHandler = this.phoneChangeHandler.bind(this);
        this.removeCustomerMediaHandler = this.removeCustomerMediaHandler.bind(this);
        this.uploadCustomerFileHandler = this.uploadCustomerFileHandler.bind(this);
        this.inputChangeHandler = this.inputChangeHandler.bind(this);
        this.clearNotesHandler = this.clearNotesHandler.bind(this);
        this.clearAddressSearchHandler = this.clearAddressSearchHandler.bind(this);
        this.searchBillingLocationHandler = this.searchBillingLocationHandler.bind(this);
        this.fetchAddressFromGoogle = this.fetchAddressFromGoogle.bind(this);
        this.billBlockCustomerHandler = this.billBlockCustomerHandler.bind(this);
        this.syncQuickBooksHandler = this.syncQuickBooksHandler.bind(this);
        this.bindBillingAddress = this.bindBillingAddress.bind(this);
        this.setFieldforForm = this.setFieldforForm.bind(this);
        this.setSaleTaxHandler = this.setSaleTaxHandler.bind(this);
        this.visitChargeHandler = this.visitChargeHandler.bind(this);
        this.showLocationName = this.showLocationName.bind(this);
        this.fetchVisitTiers = this.fetchVisitTiers.bind(this);
        this.onTabClickHandler = this.onTabClickHandler.bind(this);
        this.getAppliedQBSetting = this.getAppliedQBSetting.bind(this);
        this.removeAlternateConatct = this.removeAlternateConatct.bind(this);
        this.addAlternateContact = this.addAlternateContact.bind(this);
        this.alternateContactChangeHandler = this.alternateContactChangeHandler.bind(this);
        this.onSelectPaymentTerm = this.onSelectPaymentTerm.bind(this);
    }
    getInitialState() {
        return {
            scanning: false,
            results: []
        }
    }
    render() {
        return (
            <div>
                <button onClick={this._scan}>{this.state.scanning ? 'Stop' : 'Start'}</button>
                <ul className="results">
                    {this.state.results.map((result) => (<Result key={result.codeResult.code} result={result} />))}
                </ul>
                {this.state.scanning ? <Scanner onDetected={this._onDetected} /> : null}
            </div>
        );
    }

    _scan() {
        this.setState({ scanning: !this.state.scanning });
    }

    _onDetected(result) {
        this.setState({ results: this.state.results.concat([result]) });
    }
}
export default App;
